params.query:
-- Update existing categories to match the new standard
UPDATE menu SET category = 'Main' WHERE category = 'Main Course';
UPDATE menu SET category = 'Rice' WHERE category = 'Rice Dishes';

-- Show current menu items with their categories
SELECT id, name, category FROM menu ORDER BY category, name;

-- Step 1: Create new menu_item_spice_levels table with integer IDs
CREATE TABLE menu_item_spice_levels_new (
  id SERIAL PRIMARY KEY,
  menu_item_id INTEGER NOT NULL REFERENCES menu(id) ON DELETE CASCADE,
  user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE,
  session_id TEXT,
  spice_level INTEGER NOT NULL DEFAULT 1 CHECK (spice_level >= 0 AND spice_level <= 3),
  created_at TIMESTAMPTZ DEFAULT now(),
  updated_at TIMESTAMPTZ DEFAULT now(),
  UNIQUE(menu_item_id, user_id),
  UNIQUE(menu_item_id, session_id)
);

-- Step 2: Enable RLS on new table
ALTER TABLE menu_item_spice_levels_new ENABLE ROW LEVEL SECURITY;

-- Step 3: Create RLS policies for new table
CREATE POLICY "Users can view their own spice levels" 
  ON menu_item_spice_levels_new FOR SELECT 
  USING (
    (auth.uid() IS NOT NULL AND user_id = auth.uid()) OR
    (auth.uid() IS NULL AND session_id IS NOT NULL)
  );

CREATE POLICY "Users can insert their own spice levels" 
  ON menu_item_spice_levels_new FOR INSERT 
  WITH CHECK (
    (auth.uid() IS NOT NULL AND user_id = auth.uid()) OR
    (auth.uid() IS NULL AND session_id IS NOT NULL)
  );

CREATE POLICY "Users can update their own spice levels" 
  ON menu_item_spice_levels_new FOR UPDATE 
  USING (
    (auth.uid() IS NOT NULL AND user_id = auth.uid()) OR
    (auth.uid() IS NULL AND session_id IS NOT NULL)
  );

CREATE POLICY "Users can delete their own spice levels" 
  ON menu_item_spice_levels_new FOR DELETE 
  USING (
    (auth.uid() IS NOT NULL AND user_id = auth.uid()) OR
    (auth.uid() IS NULL AND session_id IS NOT NULL)
  );

-- Step 4: Drop old table
DROP TABLE IF EXISTS menu_item_spice_levels CASCADE;

-- Step 5: Rename new table to original name
ALTER TABLE menu_item_spice_levels_new RENAME TO menu_item_spice_levels;

-- Step 6: Create indexes for better performance
CREATE INDEX idx_menu_item_spice_levels_menu_item_id ON menu_item_spice_levels(menu_item_id);
CREATE INDEX idx_menu_item_spice_levels_user_id ON menu_item_spice_levels(user_id);
CREATE INDEX idx_menu_item_spice_levels_session_id ON menu_item_spice_levels(session_id);
